pragma solidity >=0.0;
contract C {
  function f2(uint a) public pure returns (uint r) { r = f1(a); }
  function f1(uint a) public pure returns (uint r) { r = f2(a); }
  function f0(uint a) public pure returns (uint r) { r = f1(a); }

  function g0(uint a) public pure returns (uint) {
      return f0(a);
  }
}

contract D {
  function f1(uint a) public pure returns (uint r) { r = f0(a); }
  function f0(uint a) public pure returns (uint r) { r = f1(a); }

  function g0(uint a) public pure returns (uint) {
      return f0(a);
  }

  uint public g;
  constructor(uint a) {
    g = f0(a);
  }

}
